custom_css:
/* Example CSS for your font: 20suizhengzhidangdazhinian */

/* Apply to all text */
:root * {
	--font-default: '20suizhengzhidangdazhinian';
	--default-font: '20suizhengzhidangdazhinian';
	--font-family-editor: '20suizhengzhidangdazhinian';
	--font-interface-override: '20suizhengzhidangdazhinian';
	--font-text-override: '20suizhengzhidangdazhinian';
}

/* Example: Apply to custom CSS class */
.custom-font * {
	font-family: '20suizhengzhidangdazhinian' !important;
}

/* Example: Apply to specific elements only */
.custom-font h1, .custom-font h2, .custom-font h3 {
	font-family: '20suizhengzhidangdazhinian' !important;
}
